<?php/** "News" block  *  * Optional to use horizontal lines/images**/class AQ_Advertise_Block extends AQ_Block {		//set and create block	function __construct() {		$block_options = array(			'name' => 'Advertise',
			'size' => 'span12',
			'resizable' => 0,			'icon' => 'fa-square-o',			'icon_color' => 'FFF',			'category' => 'Content',			'help' => 'This is the block that adds advertising block. You add advertisers in content tab in Admin Panel of the Theme.'		);				//create the block		parent::__construct('aq_advertise_block', $block_options);					}		function form($instance) {				$defaults = array(			'speed' => 5000		);						$instance = wp_parse_args($instance, $defaults);		extract($instance);				if( function_exists( 'pmc_advertiseBlock' ) ){		?>		<p class="description note">			<?php _e('Use this block to create advertising block.', 'pmc-themes') ?>		</p>		<div class="description">			<label for="<?php echo $this->get_field_id('title') ?>">				Slider speed				<?php echo aq_field_input('speed', $block_id, $speed) ?>			</label>		</div>
			<?php		}		else {			echo '<p class="description note">For this block you need to use PremiumCoding themes!</p>';		}			}		function block($instance) {		$defaults = array(			'speed' => 5000		);						$instance = wp_parse_args($instance, $defaults);		extract($instance);				if( function_exists( 'pmc_advertiseBlock' ) ){			pmc_advertiseBlock($speed);		}}}