<?php/** "News" block  *  * Optional to use horizontal lines/images**/class PMC_Woo_Cart_Block extends AQ_Block {		//set and create block	function __construct() {		$block_options = array(			'name' => 'Woo mini cart',			'size' => 'span3',			'resizable' => 1,			'icon' => 'fa-th-list',			'icon_color' => 'FFF',			'category' => 'Ecommerce',			'help' => 'This is the block that adds small cart (used in header).'		);				//create the block		parent::__construct('pmc_woo_cart_block', $block_options);	}		function form($instance) {				$defaults = array();					$instance = wp_parse_args($instance, $defaults);			extract($instance);			?>		<p class="description note">			<?php _e('Use this block to create small cart', 'pmc-themes') ?>		</p>		<?php			}		function block($instance) {			global $woocommerce, $pmc_data, $sitepress,$product; ?>		<div class="cartWrapper">			<?php 			$check_out = '';			if($woocommerce->cart->get_cart_url() != ''){ 				$cart=$woocommerce->cart->get_cart_url();				$check_out = $woocommerce->cart->get_checkout_url(); 			}			else {$cart = home_url().'cart/';};			?>			<div class="header-cart-left">				<div class="header-cart-items">					<a href="<?php echo $cart; ?>" class="cart-top"><?php   _e('Items', 'pmc-themes');  ?></a>					<a class="cart-bubble cart-contents">(<?php echo sprintf(_n('%d', '%d', $woocommerce->cart->cart_contents_count, 'pmc-themes'), $woocommerce->cart->cart_contents_count);?>)</a>				</div>				<div class="header-cart-total">					<a class="cart-total"><?php echo $woocommerce->cart->get_cart_total(); ?></a>				</div>			</div>			<div class="header-cart-icon"></div>			<div class="cartTopDetails">				<div class="cart_list product_list_widget">					<div class="widget_shopping_cart_top widget_shopping_cart_content">							<?php get_template_part('woocommerce/cart/mini-cart') ?>					</div>					</div>				</div>		</div>		<?php					}}?>