<?php/** * PMC Builder functions ***/if(class_exists('AQ_Page_Builder')) {		/** 	 * Core functions	*******************/	 	/* Register a block */	function aq_register_block($block_class) {		global $aq_registered_blocks;		$aq_registered_blocks[strtolower($block_class)] = new $block_class;	}		/** Un-register a block **/	function aq_unregister_block($block_class) {		global $aq_registered_blocks;		$block_class = strtolower($block_class);		foreach($aq_registered_blocks as $block) {			if($block->id_base == $block_class) unset($aq_registered_blocks[$block_class]);		}	}		/** Get list of all blocks **/	function aq_get_blocks($template_id) {		global $aq_page_builder;		$blocks = $aq_page_builder->get_blocks($template_id);				return $blocks;	}	function aq_resize( $url, $width, $height = null, $crop = null, $single = true ) {		//validate inputs		if(!$url OR !$width ) return false;		//define upload path & dir		$upload_info = wp_upload_dir();		$upload_dir = $upload_info['basedir'];		$upload_url = $upload_info['baseurl'];		//check if $img_url is local		if(strpos( $url, $upload_url ) === false) return false;		//define path of image		$rel_path = str_replace( $upload_url, '', $url);		$img_path = $upload_dir . $rel_path;		//check if img path exists, and is an image indeed		if( !file_exists($img_path) OR !getimagesize($img_path) ) return false;		//get image info		$info = pathinfo($img_path);		$ext = $info['extension'];		list($orig_w,$orig_h) = getimagesize($img_path);		//get image size after cropping		$dims = image_resize_dimensions($orig_w, $orig_h, $width, $height, $crop);		$dst_w = $dims[4];		$dst_h = $dims[5];		//use this to check if cropped image already exists, so we can return that instead		$suffix = "{$dst_w}x{$dst_h}";		$dst_rel_path = str_replace( '.'.$ext, '', $rel_path);		$destfilename = "{$upload_dir}{$dst_rel_path}-{$suffix}.{$ext}";		if(!$dst_h) {			//can't resize, so return original url			$img_url = $url;			$dst_w = $orig_w;			$dst_h = $orig_h;		}		//else check if cache exists		elseif(file_exists($destfilename) && getimagesize($destfilename)) {			$img_url = "{$upload_url}{$dst_rel_path}-{$suffix}.{$ext}";		} 		//else, we resize the image and return the new resized image url		else {			// Note: This pre-3.5 fallback check will edited out in subsequent version			if(function_exists('wp_get_image_editor')) {				$editor = wp_get_image_editor($img_path);				if ( is_wp_error( $editor ) || is_wp_error( $editor->resize( $width, $height, $crop ) ) )					return false;				$resized_file = $editor->save();				if(!is_wp_error($resized_file)) {					$resized_rel_path = str_replace( $upload_dir, '', $resized_file['path']);					$img_url = $upload_url . $resized_rel_path;				} else {					return false;				}			} else {				$resized_img_path = image_resize( $img_path, $width, $height, $crop ); // Fallback foo				if(!is_wp_error($resized_img_path)) {					$resized_rel_path = str_replace( $upload_dir, '', $resized_img_path);					$img_url = $upload_url . $resized_rel_path;				} else {					return false;				}			}		}		//return the output		if($single) {			//str return			$image = $img_url;		} else {			//array return			$image = array (				0 => $img_url,				1 => $dst_w,				2 => $dst_h			);		}		return $image;	}		/** 	 * Form Field Helper functions	 *	 * Provides some default fields for use in the blocks	********************************************************/		/* Input field - Options: $size = min, small, full */	function aq_field_input($field_id, $block_id, $input, $size = 'full', $type = 'text') {		$output = '<input type="'.$type.'" id="'. $block_id .'_'.$field_id.'" class="input-'.$size.'" value="'.$input.'" name="aq_blocks['.$block_id.']['.$field_id.']">';				return $output;	}		/* Textarea field */	function aq_field_textarea($field_id, $block_id, $text, $size = 'full') {		$output = '<textarea id="'. $block_id .'_'.$field_id.'" class="textarea-'.$size.'" name="aq_blocks['.$block_id.']['.$field_id.']" rows="5">'.$text.'</textarea>';				return $output;	}			/* Select field */	function aq_field_select($field_id, $block_id, $options, $selected) {		$options = is_array($options) ? $options : array();		$output = '<select id="'. $block_id .'_'.$field_id.'" name="aq_blocks['.$block_id.']['.$field_id.']">';		foreach($options as $key=>$value) {			$output .= '<option value="'.$key.'" '.selected( $selected, $key, false ).'>'.htmlspecialchars($value).'</option>';		}		$output .= '</select>';				return $output;	}		/* Multiselect field */	function aq_field_multiselect($field_id, $block_id, $options, $selected_keys = array()) {		$output = '<select id="'. $block_id .'_'.$field_id.'" multiple="multiple" class="select of-input" name="aq_blocks['.$block_id.']['.$field_id.'][]">';		foreach ($options as $key => $option) {			$selected = (is_array($selected_keys) && in_array($key, $selected_keys)) ? $selected = 'selected="selected"' : '';						$output .= '<option id="'. $block_id .'_'.$field_id.'_'. $key .'" value="'.$key.'" '. $selected .' />'.$option.'</option>';		}		$output .= '</select>';				return $output;	}		/* Color picker field */	function aq_field_color_picker($field_id, $block_id, $color, $default = '') {		$output = '<div class="aqpb-color-picker">';			$output .= '<input type="text" id="'. $block_id .'_'.$field_id.'" class="input-color-picker" value="'. $color .'" name="aq_blocks['.$block_id.']['.$field_id.']" data-default-color="'. $default .'"/>';		$output .= '</div>';				return $output;	}		/* Single Checkbox */	function aq_field_checkbox($field_id, $block_id, $check) {		$output = '<input type="hidden" name="aq_blocks['.$block_id.']['.$field_id.']" value="0" />';		$output .= '<input type="checkbox" id="'. $block_id .'_'.$field_id.'" class="input-checkbox" name="aq_blocks['.$block_id.']['.$field_id.']" '. checked( 1, $check, false ) .' value="1"/>';				return $output;	}		/* Multi Checkbox */	function aq_field_multicheck($field_id, $block_id, $fields = array(), $selected = array()) {		}		/* Media Uploader */	function aq_field_upload($field_id, $block_id, $media, $media_type = '') {		$output = '<input type="text" id="'. $block_id .'_'.$field_id.'" class="input-full input-upload" value="'.$media.'" name="aq_blocks['.$block_id.']['.$field_id.']">';		$output .= '<a href="#" class="aq_upload_button button" rel="'.$media_type.'">Upload</a><p></p>';				return $output;	}		/** 	 * Misc Helper Functions	**************************/		/** Get column width	 * @parameters - $size (column size), $grid (grid size e.g 940), $margin	 */	function aq_get_column_width($size, $grid = 940, $margin = 20) {				$columns = range(1,12);		$widths = array();		foreach($columns as $column) {			$width = (( $grid + $margin ) / 12 * $column) - $margin;			$width = round($width);			$widths[$column] = $width;		}				$column_id = absint(preg_replace("/[^0-9]/", '', $size));		$column_width = $widths[$column_id];		return $column_width;	}		/** Recursive sanitize	 * For those complex multidim arrays 	 * Has impact on server load on template save, so use only where necessary 	 */	function aq_recursive_sanitize($value) {		if(is_array($value)) {			$value = array_map('aq_recursive_sanitize', $value);		} else {			$value = htmlspecialchars(stripslashes($value));		}		return $value;	}		function pmc_inner_block($content,$instance,$title,$icon){		$rand = rand(100,999);		echo '<div id="template-block-'.$rand.'" class="description prebuild ">',				'<dl class="block-bar">',					'<dt class="block-handle">',						'<div class="block-title">',							'<i class="fa '.$icon.' " style="color:FFF"></i>'.$title.						'</div>',						'<span class="block-controls">',							'<a class="block-edit inner" id="edit-'.$rand.'" title="Edit Block" href="#block-settings-'.$rand.'">Edit Block</a>',						'</span>',					'</dt>',				'</dl>',				'<div class="block-settings cf" id="block-settings-'.$rand.'">';				$content -> form($instance);		echo '</div>',			'</div>';			}	/* function for portfolio block*/	function pmc_portBlock($title,$number_post,$rowsB,$categories,$port_ajax,$id,$show_navigation = 1){		wp_enqueue_script('pmc_bxSlider');				global $pmc_data;		if(isset($pmc_data['home_recent_number_post'])){			$showpost = $pmc_data['home_recent_number_post'] ;}		else{			$showpost = 3;}					if($number_post){			$showpost = $number_post  ;}				if($title) {			$title = $title;		}		else {			$title = '';		}					if(isset($pmc_data['home_recent_number_display']))			$rows = $pmc_data['home_recent_number_display'];		else			$rows = 3;					if($rowsB){			$rows = $rowsB;		}					if($categories)			$categories = $categories;		else			$categories='';						$pc = new WP_Query(array('post_type' => $pmc_data['port_slug'],					'tax_query' => array( 						 array (						  'taxonomy' => 'portfoliocategory',						  'field' => 'id',						  'terms' => $categories						 ), 					 ),					'posts_per_page' => $number_post)				 );		?>		<script type="text/javascript">		jQuery(document).ready(function($){			jQuery('.sliderAdvertisePort').bxSlider({			  auto: true,			  speed: 1000,			  <?php if($show_navigation == 0){ ?>			  controls: false,			  <?php } ?>			  pager :false,				prevText : '<i class="fa fa-angle-left"></i>',				nextText : '<i class="fa fa-angle-right"></i>',			  easing : 'easeInOutQuint',			});		});			</script>			<?php 		if ($pc->have_posts()) :	wp_enqueue_script('pmc_bxSlider');	?>	<div class="homerecent">		<div class="homerecentInner">		<div id = "showpost-<?php echo $pmc_data['port_slug'] ?>">			<div class="showpostload"><div class="loading"></div></div>			<div class = "closehomeshow-<?php echo $pmc_data['port_slug'] ?> port closeajax"><i class="fa fa-times"></i></div>			<div class="showpostpostcontent"></div>		</div>			<ul id="sliderAdvertisePort" class="sliderAdvertisePort">			<?php			$currentindex = '';			$count = 1;			$countitem = 1;			$type = $pmc_data['port_slug'];			?>			<?php  while ($pc->have_posts()) : $pc->the_post();			if($countitem == 1){				echo '<li>';}						$full_image = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), 'homePort', false);				$catType= 'portfoliocategory';						//category			$categoryIn = get_the_term_list( get_the_ID(), $catType, '', ', ', '' );				$category = explode(',',$categoryIn);				//end category						if ( has_post_thumbnail() ){				$image = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), 'homePort', false);				$image = $image[0];				$image_big = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), 'homePortBig', false);								$image_big = $image_big[0];				$imagefull = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), 'full', false);				$imagefull = $imagefull[0];							}			?>				<div class="one_third <?php if($countitem == 2) echo 'big' ?> <?php if($countitem == 4) echo 'first'; ?>" >									<?php if ($port_ajax == 'true'){ ?>					<div class="click" id="<?php echo $type ?>_<?php echo get_the_id() ?>">					<?php } ?>						<div class="recentimage">							<div class="overdefult">															<div class="recentdescription">									<?php if ($port_ajax != 'true'){ ?>										<a href = "<?php echo get_permalink( get_the_id()) ?>" title="<?php echo esc_attr(  get_the_title(get_the_id()) ) ?>"  >									<?php } ?>									<h3><?php the_title() ?></h3>									<?php if ($port_ajax != 'true'){ ?>										</a>									<?php } ?>										<div class="home-portfolio-categories"><?php echo strip_tags(get_the_term_list( get_the_ID(), 'portfoliocategory', '', ', ', '' )); ?></div>								</div>															</div>										<div class="image">																		<?php if ($port_ajax != 'true'){ ?>										<a href = "<?php echo get_permalink( get_the_id()) ?>" title="<?php echo esc_attr(  get_the_title(get_the_id()) ) ?>"  >									<?php } ?>																			<?php if (isset($image)){ ?>										<?php if($countitem == 2) { ?>											<img class="portfolio-home-image" src="<?php echo $image_big ?>" alt="<?php the_title(); ?>">										<?php } else { ?>																					<img class="portfolio-home-image" src="<?php echo $image ?>" alt="<?php the_title(); ?>">																				<?php } ?>										<?php } else  { ?>											<img class="portfolio-home-image" src="http://placehold.it/393x300" alt="<?php the_title(); ?>">									<?php } ?>										<?php if ($port_ajax != 'true'){ ?>										</a>									<?php } ?>								</div>						</div>									<?php if ($port_ajax == 'true'){ ?>						</div>					<?php } ?>				</div>			<?php 			$count++;						 if($countitem == 5){ 				$countitem = 0; ?>				</li>			<?php } 			$countitem++;			endwhile; 			wp_reset_query(); ?>			</ul>		</div>	</div>	<?php  endif; ?>	<div class="clear"></div>	<?php	}		/*portfolio loop*/	function pmc_portfolio($portSize, $item, $post = 'port' ,$number = 0,$cat = '',$port_ajax, $style = 'default',$title = 1, $excerpt = 1,$home = '',$masonry = 0, $related_port = '', $infinite = 0,$paged_in = 1,$lightbox = 0){		wp_enqueue_script('pmc_isotope');				global $pmc_data; 		$categport = '';		$paged = (get_query_var('paged')) ? get_query_var('paged') : $paged_in;		if($post == 'port'){			$postT = $pmc_data['port_slug'];			$postC = 'portfoliocategory';			$showPost = $pmc_data['port_slug'];		} else {			$postT = 'post';			$postC = 'category';				$showPost = 'post';		}				if(isset($infinite) && $infinite == 1){			//$number = -1;		}					if($cat != ''){					$args = array(				'tax_query' => array(array('taxonomy' => $postC,'field' => 'id','terms' => $cat)),				'showposts'     => $number,				'post_type' => $postT,				'paged'    => $paged				);						}		else{				$args = array(				'showposts'     => $number,				'post_type' => $postT,				'paged'    => $paged				);			}		query_posts( $args );		if($item == 4){			$columns = 'one_fourth';			$image_width = 'port4';			$limit = 20;			$img_par = ' width="280px" height="220px" ';			$video_width = '280';			$video_height = '220';		}		if($item == 3){			$columns = 'one_third';			if($home == 'homePort'){				$image_width = 'homePort';			} else {				$image_width = 'port3';			}			$limit = 26;			$img_par = ' width="380px" height="300px" ';			$video_width = '380';			$video_height = '300';					}			if($item == 2){			$columns = 'one_half';			$image_width = 'port2';			$limit = 35;			$img_par = ' width="570px" height="450px" ';			$video_width = '570';			$video_height = '450';					}		if(isset($style) && $style == 'circle'){			$image_width = 'port-circle';			$img_par = ' width="275px" height="275px" ';					}		$new_class = 'homePort';		if($home != 'homePort'){			$new_class =  'pagePort'; 			} else{			$img_par = ' width="393px" height="300px" ';			$video_width = '393';			$video_height = '300';						}						$masonry_pmc = '';		if($masonry == 1){			$masonry_pmc =  'masonry_pmc'; 		} 				$currentindex = $linkPost = '';		$currentindex = '';		$count = 1;		$countitem = 1;		?>		<div class="homerecent <?php echo $new_class ?>">		<div class="homerecentInner <?php echo $style ?>">		<div id = "showpost-<?php echo $showPost ?>">			<div class="showpostload"><div class="loading"></div></div>			<div class = "closehomeshow-<?php echo $showPost ?> <?php echo $post ?> closeajax"><i class="fa fa-times"></i></div>			<div class="showpostpostcontent"></div>		</div>			<div class="portfolio">				<div id="portitems4">					<?php					while ( have_posts() ) : the_post();						$postmeta = get_post_custom(get_the_ID()); 													$do_not_duplicate = get_the_ID(); 						if ( has_post_thumbnail() ){							$image = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), $image_width , false);							$image = $image[0];														$imagefull = wp_get_attachment_image_src(get_post_thumbnail_id(get_the_ID()), 'full', false);							$imagefull = $imagefull[0];															}						$full_image = wp_get_attachment_image_src(get_post_thumbnail_id(), 'full', false);						$entrycategory = get_the_term_list( get_the_ID(), $postC, '', ',', '' );						$catstring = $entrycategory;						$catstring = strip_tags($catstring);						$catidlist = explode(",", $catstring);						$catlist = '';						for($i = 0; $i < sizeof($catidlist); ++$i){							$catidlist[$i].=$currentindex;							$find =     array("&", "/", " ","amp;","&#38;");							$replace  = array("", "", "", "","");										$catlist .= str_replace($find,$replace,$catidlist[$i]). ' ';													}						$categoryIn = get_the_term_list( get_the_ID(), $postC, '', ', ', '' );							$category = explode(',',$categoryIn);							if ( has_post_format( 'link' , get_the_ID()) and $post == 'post') {							if(isset($postmeta["link_post_url"][0] )) $linkPost = $postmeta["link_post_url"][0];							}						else{							if (function_exists('icl_object_id')) 								$linkPost = get_permalink(icl_object_id(get_the_ID(), $pmc_data['port_slug'], true, true));							else 								$linkPost = get_permalink();						}														if($count != ($item+1)){							echo '<div class="'.$columns.' item'.$item.' '.$catlist .' '.$masonry_pmc.'" data-category="'. $catlist.'" >';													}						else{							echo '<div class="'.$columns.' last item'.$item.' '.$catlist .' '.$masonry_pmc.'" data-category="'. $catlist.'" >';							$count = 0;						}						if($lightbox == 1){							$link = 'href = "'.$imagefull . '" rel="lightbox"';							$link_title = 'href = "'.get_permalink( get_the_id()).'"';						}else if(isset($postmeta["out_link"][0] ) && $postmeta['out_link'][0] == 1){							$link = 'href = "'.$postmeta['website_url'][0].'" target = "_blank"';							$link_title = 'href = "'.$postmeta['website_url'][0].'" target = "_blank"';													}						else {							$link = 'href = "'.get_permalink( get_the_id()).'" ';							$link_title = 'href = "'.get_permalink( get_the_id()).'" ';												}																														?>								<?php if ($port_ajax == 'true' && !has_post_format( 'link' , get_the_id())){ ?>								<div class="click" id="<?php echo $showPost ?>_<?php echo get_the_id() ?>">								<?php } ?>												<div class="recentimage">										<?php if ($port_ajax != 'true' || has_post_format( 'link' , get_the_id())){ ?>											<?php if (has_post_format( 'link' , get_the_id())) { ?>												<a href = "<?php echo $postmeta["link_post_url"][0] ?>" title="<?php echo esc_attr(  get_the_title(get_the_id()) ) ?>"  >											<?php } else { ?>												<a <?php echo $link ?> title="<?php echo esc_attr(  get_the_title(get_the_id()) ) ?>"  >											<?php } ?>										<?php } ?>												<?php if (!isset($postmeta['show_video'][0]) || $postmeta['show_video'][0] != 1) {?>											<div class="overdefult">																					<div class="recentdescription">																						<?php if($home == 'homePort'){ ?>													<h3><?php the_title() ?></h3>																										<div class ="border-line"></div>													<div class="home-portfolio-categories"><?php echo strip_tags(get_the_term_list( get_the_ID(), $postC, '', ', ', '' )); ?></div>												<?php } else {?>													<?php if($masonry != 1){ ?>														<div class="home-portfolio-categories pagePort"><?php echo strip_tags(get_the_term_list( get_the_ID(), $postC, '', ', ', '' )); ?></div>													<?php } ?>												<?php } ?>																			</div>										</div>											<?php } ?>										<?php if ($port_ajax != 'true' || has_post_format( 'link' , get_the_id())){ ?>											</a>										<?php } ?>																				<div class="image">											<?php if ($port_ajax != 'true' || has_post_format( 'link' , get_the_id())){ ?>												<?php if (has_post