itle(); ?></a></h2>				<div class = "post-meta">					<?php _e('By ','pmc-themes'); ?>					<?php echo get_the_author_link() ?>					<?php _e(' on ','pmc-themes'); ?>					<span><?php the_time('F j, Y') ?></span>				</div>			</div>						<div class="blogContent">				<div class="blogcontent"><?php echo content($limit) ?></div>				<a class="blogmore" href="<?php the_permalink() ?>"><?php _e('Read more','pmc-themes'); ?> </a>			</div>		</div>			</div>		<?php}/*custom excerpt*/function excerpt($limit) {      $excerpt = explode(' ', get_the_excerpt(), $limit);      if (count($excerpt)>=$limit) {        array_pop($excerpt);        $excerpt = implode(" ",$excerpt).'...';      } else {        $excerpt = implode(" ",$excerpt);      }       $excerpt = preg_replace('`\[[^\]]*\]`','',$excerpt);      return $excerpt;    }function content($limit) {      $content = explode(' ', get_the_content(), $limit);      if (count($content)>=$limit) {        array_pop($content);        $content = implode(" ",$content).'...';      } else {        $content = implode(" ",$content);      }       $content = preg_replace('/\[.*?\]/','', $content);      $content = apply_filters('the_content', $content);       $content = str_replace(']]>', ']]&gt;', $content);      return $content;}/*check if array is empty*/function pmc_is_array_empty($arr){  if(is_array($arr)){           foreach($arr as $key => $value){          if(!empty($value) || $value != NULL || $value != ""){              return true;              break;//stop the process we have seen that at least 1 of the array has value so its not empty          }      }      return false;  }}function pmc_woo(){	if ( in_array( 'woocommerce/woocommerce.php', apply_filters( 'active_plugins', get_option( 'active_plugins' ) ) ) ) {		return true;	}}if(pmc_woo() && PMC_SHOP){		/*set header and footer*/	remove_action( 'woocommerce_before_main_content', 'woocommerce_output_content_wrapper', 10);	remove_action( 'woocommerce_after_main_content', 'woocommerce_output_content_wrapper_end', 10);	add_action('woocommerce_before_main_content', 'pmc_wrapper_start', 10);	add_action('woocommerce_after_main_content', 'pmc_wrapper_end', 10);	function pmc_wrapper_start() { 		echo'			<!-- main content start -->			<div class="mainwrap">				<div class="main clearfix">					<div class="content fullwidth">						<div class="postcontent">							<div class="posttext">';		}	//This line ends the new "woogrid" section	function pmc_wrapper_end() { 		echo'				</div>						</div>					</div>				</div>			</div>';	}				/*add wishlist*/	add_filter( 'woocommerce_before_shop_loop_item', 'pmc_custom_product_category' );	function pmc_custom_product_category($product) {		global $wpdb, $product;		$metakey= get_post_meta( get_the_ID(), '_meta_key', true );		if($metakey!= NULL) {			return $product;		 } else {		if(class_exists( 'YITH_WCWL_Shortcode' )){			echo do_shortcode( '[yith_wcwl_add_to_wishlist]' );		}	  }	}		add_filter('add_to_cart_fragments', 'woocommerce_header_add_to_cart_fragment');	function woocommerce_header_add_to_cart_fragment( $fragments ) {		global $woocommerce;		ob_start();		?>		<a class="cart-bubble cart-contents">(<?php echo sprintf(_n('%d', '%d', $woocommerce->cart->cart_contents_count, 'pmc-themes'), $woocommerce->cart->cart_contents_count);?>)</a>		<?php		$fragments['a.cart-contents'] = ob_get_clean();		return $fragments;	}				}/*builder menu*/class pmc_Walker_Builder extends Walker_Nav_Menu{	  function start_el(&$output, $item, $depth = 0, $args = array(), $id = 0) {		   global $wp_query;		   $attributes = '';		   $indent = ( $depth ) ? str_repeat( "\t", $depth ) : '';		   $class_names = $value = '';		   $classes = empty( $item->classes ) ? array() : (array) $item->classes;		   $class_names = join( ' ', apply_filters( 'nav_menu_css_class', array_filter( $classes ), $item ) );		   $class_names = ' class="'. esc_attr( $class_names ) . '"';		   $output .= $indent . '<li id="menu-item-'.rand(0,9999).'-'. $item->ID . '"' . $value . $class_names .'>';		   $attributes_title  = ! empty( $item->attr_title ) ? ' <i class="fa '  . esc_attr( $item->attr_title ) .'"></i>' : '';		   $attributes .= ! empty( $item->url )        ? ' href="'   . 								esc_url(add_query_arg(									array(										'page' => 'aq-page-builder', 										'action' => 'edit',										'template' => $item->object_id,									),									admin_url( 'themes.php' )								)) .'"' : '';		   		   $prepend = '';		   $append = '';		   if($depth != 0)		   {				$append = $prepend = "";		   }			$item_output = $args->before;			$item_output .= '<a '. $attributes .'>';			$item_output .= $attributes_title.$args->link_before .$prepend.apply_filters( 'the_title', $item->title, $item->ID ).$append;			$item_output .= $args->link_after;			$item_output .= '</a>';				$item_output .= $args->after;			$output .= apply_filters( 'walker_nav_menu_start_el', $item_output, $item, $depth, $args );	}}	function pmc_custom_help() {	global $post_ID;	$screen = get_current_screen();	$screen->add_help_tab( array(	'id' => 'pmc_shortcode', //unique id for the tab	'title' => 'PMC shortcode', //unique visible title for the tab	'content' => pmc_shortcode_help(), //actual help text	));}add_action('admin_head', 'pmc_custom_help');function pmc_shortcode_generator(){$rand = rand(0,999);?><div id="select-shortcode"class="select-shortcode">	<div class="font-awesome-help">For icons name go to <a target="_blank" href="http://fontawesome.io/icons/">Font Awesome</a>. Use name like fa-button-name.</div>	<div class="shortcode-help">Need <b>quick help</b> for shorcode?</div>	<div id="remove" class="builderremove-<?php echo $rand ?>" data-option-key="filter">		<a class="catlink" href="#filter" data-option-value="*">Show All</a>		<a class="catlink" href="#filter" data-option-value=".columns">Columns</a>		<a class="catlin